import React, { useState } from 'react'
import { Input,Popover,Radio,Modal,message } from "antd";
import { ArrowDownOutlined,DownOutlined,SettingOutlined } from "@ant-design/icons";
import tokenList from "../tokenList.json";

function Swap() {
const [slippage,setSlippage] =useState()
const [tokenOneAmount,settokenOneAmount] = useState(null)
const[tokenTwoAmount,settokenTwoAmount] = useState(null)
const[tokenOne,settokenOne]=useState(tokenList[0])
const[tokenTwo,setTokenTwo]= useState(tokenList[1])

function handleSlippage(e){
  setSlippage(e.target.value)
}
function changeAmount(e){
  e&&settokenOneAmount(e.target.value)
}
  const setting =(<>
  <div>Slippage Tolerance</div>
  <div>
    <Radio.Group value={slippage} onChange={handleSlippage}  >
      <Radio.Button value={0.5} >0.5%</Radio.Button>
      <Radio.Button value={2.5} >2.5%</Radio.Button>

      <Radio.Button value={5} >5.0%</Radio.Button>

</Radio.Group>
  </div>
  </>)
  return (
    <div className='tradeBox' >
      <div className='tradeBoxHeader' >
        <h4>Swap</h4>
        <Popover content={setting} title="Setting" trigger="click" placement='bottomRight' ><SettingOutlined className='cog' ></SettingOutlined></Popover>
         </div>
         <div className='inputs' >
          <Input placeholder='0' value={tokenOneAmount} onChange={changeAmount()} ></Input>
          <Input placeholder='0' value={tokenTwoAmount} disabled={true}></Input>
          <div className='asseetOne' >
            <img src='tokenOne.img' alt="tokenOne" className='assetLogo' />
            {tokenOne.ticker}
            <DownOutlined/>
          </div>
          <div className='asseetTwo' >
            <img src='tokenTwo.img' alt="tokenTwo" className='assetLogo' />
            {tokenTwo.ticker}
            <DownOutlined/>
          </div>
         </div>
        </div>
  )
}

export default Swap
